import React, { useEffect, useState } from 'react';
import AdicionarContato from '../adicionarContato';
import logo from '../../assets/logo.png';

const ListaContatos = () => {
  const [contatos, setContatos] = useState([]);
  const [contatoSelecionado, setContatoSelecionado] = useState(null);
  const [buscaContato, setBuscaContato] = useState('');

  useEffect(() => {
    const fetchContatos = async () => {
      try {
        const response = await fetch('https://my.api.mockaroo.com/lestetelecom/test.json?key=f55c4060');
        const data = await response.json();
        setContatos(data);
      } catch (error) {
        console.error('Erro ao obter os contatos:', error);
      }
    };

    fetchContatos();
  }, []);

  const handleEditarClick = (id) => {
    const contato = contatos.find((contato) => contato.id === id);
    setContatoSelecionado(contato);
  };

  const handleExcluirClick = (id) => {
    const confirmarExclusao = window.confirm("Tem certeza que deseja excluir o contato?");
    if (confirmarExclusao) {
      console.log('Excluir contato:', id);
      setContatos((prevContatos) => prevContatos.filter((contato) => contato.id !== id));
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setContatoSelecionado((prevContato) => ({
      ...prevContato,
      [name]: value,
    }));
  };

  const handleSalvarEdicao = () => {
    setContatos((prevContatos) => {
      return prevContatos.map((contato) => {
        if (contato.id === contatoSelecionado.id) {
          return { ...contato, ...contatoSelecionado };
        }
        return contato;
      });
    });
    setContatoSelecionado(null);
  };

  const adicionarContato = (novoContato) => {
    const contatoComID = { ...novoContato, id: Date.now() };
    setContatos((prevContatos) => [...prevContatos, contatoComID]);
  };

  const handleBuscaInputChange = (event) => {
    setBuscaContato(event.target.value);
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', gap: '5px', backgroundColor: '#EBEBEB' }}>
      <img src={logo} alt="Logo" style={{ maxWidth: '150px', maxHeight: '150px', marginBottom: '0px', marginTop: '30px', marginLeft: '350px' }} />
      <div style={{ display: 'flex', justifyContent: 'flex-end', marginRight: '10px' }}>
        <AdicionarContato onAdicionarContato={adicionarContato} />
        <input type="text" placeholder="Buscar por nome" value={buscaContato} style={{ marginTop: '40px', marginBottom:'5px'}} onChange={handleBuscaInputChange} />
      </div>

      {contatos
        .filter((contato) =>
          contato.first_name.toLowerCase().includes(buscaContato.toLowerCase()) ||
          contato.last_name.toLowerCase().includes(buscaContato.toLowerCase())
        )
        .map((contato) => (
          <div
            key={contato.id}
            style={{
              display: 'flex',
              border: contatoSelecionado && contatoSelecionado.id === contato.id ? '2px solid #0BE021' : '1px solid #12A146',
              padding: '10px'
            }}
          >
            <img src={contato.avatar} alt="Avatar" style={{ border: '1px solid #12A146', width: '100px', height: '100px', marginRight: '10px', marginTop: '30px' }} />
            <div>
              {contatoSelecionado && contatoSelecionado.id === contato.id ? (
              <>
                <div>
                  <label>Nome:</label>
                  <input type="text" name="first_name" value={contatoSelecionado.first_name} onChange={handleInputChange} />
                </div>
                <div>
                  <label>Sobrenome:</label>
                  <input type="text" name="last_name" value={contatoSelecionado.last_name} onChange={handleInputChange} />
                </div>
                <div>
                  <label>Email:</label>
                  <input type="email" name="email" value={contatoSelecionado.email} onChange={handleInputChange} />
                </div>
                <div>
                  <label>Gênero:</label>
                  <input type="text" name="gender" value={contatoSelecionado.gender} onChange={handleInputChange} />
                </div>
                <div>
                  <label>Idioma:</label>
                  <input type="text" name="language" value={contatoSelecionado.language} onChange={handleInputChange} />
                </div>
                <div>
                  <label>Data de Nascimento:</label>
                  <input type="text" name="birthday" value={contatoSelecionado.birthday} onChange={handleInputChange} />
                </div>
                <button style={{ backgroundColor: '#12A146', color: '#FFFFFF', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', marginRight: '5px', marginLeft: '0px', marginTop: '10px', marginBottom: '10px'}} onClick={handleSalvarEdicao}>Salvar</button>
              </>
            ) : (
              <>
                <p>Nome: {contato.first_name} {contato.last_name}</p>
                <p>Email: {contato.email}</p>
                <p>Gênero: {contato.gender}</p>
                <p>Idioma: {contato.language}</p>
                <p>Data de Nascimento: {contato.birthday}</p>
                <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
                  <button style={{ backgroundColor: '#12A146', color: '#FFFFFF', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', marginRight: '5px', marginLeft: '0px', marginTop: '10px', marginBottom: '10px'}} onClick={() => handleEditarClick(contato.id)}>Editar</button>
                  <button style={{ backgroundColor: '#12A146', color: '#FFFFFF', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', marginRight: '5px', marginLeft: '0px', marginTop: '10px', marginBottom: '10px'}} onClick={() => handleExcluirClick(contato.id)}>Excluir</button>
                </div>
              </>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ListaContatos;
