import React, { useState, useEffect } from 'react';
import contatos from '../ListaContatos/lis'

const EditarContato = ({ user, onSave, onCancel }) => {
  const [editedUser, setEditedUser] = useState({ ...user });

  useEffect(() => {
    setEditedUser({ ...user });
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedUser({ ...editedUser, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(editedUser);
  };

  const handleCancel = () => {
    onCancel();
  };

  return (
    <div className="editarContatoForm">
      <h2>Editar Contato</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="first_name">Nome:</label>
        <input type="text" name="first_name" value={editedUser.first_name} placeholder={user.first_name} onChange={handleChange} />

        <label htmlFor="last_name">Sobrenome:</label>
        <input type="text" name="last_name" value={editedUser.last_name} placeholder={user.last_name} onChange={handleChange} />

        <label htmlFor="birthday">Data de Nascimento:</label>
        <input type="text" name="birthday" value={editedUser.birthday} placeholder={user.birthday} onChange={handleChange} />

        {/* Adicione mais campos de entrada para outros detalhes do usuário, se necessário */}

        <button type="submit">Salvar</button>
        <button type="button" onClick={handleCancel}>Cancelar</button>
      </form>
    </div>
  );
};

export default EditarContato;
