import React, { useEffect, useState } from 'react';

const ListaContatos = () => {
  const [contatos, setContatos] = useState([]);

  useEffect(() => {
    const fetchContatos = async () => {
      try {
        const response = await fetch('https://my.api.mockaroo.com/lestetelecom/test.json?key=f55c4060');
        const data = await response.json();
        setContatos(data);
      } catch (error) {
        console.error('Erro ao obter os contatos:', error);
      }
    };

    fetchContatos();
  }, []);

  const handleEditarClick = (id) => {
    // Lógica para editar o contato com o ID fornecido
    console.log('Editar contato:', id);
  };

  const handleExcluirClick = (id) => {
    // Lógica para excluir o contato com o ID fornecido
