    setContatoSelecionado(null);
  };
  
  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', gap: '5px' }}>
      {contatos.map(contato => (
        <div key={contato.id} style={{ display: 'flex', border: '1px solid #12A146', padding: '10px' }}>
          <img src={contato.avatar} alt="Avatar" style={{ width: '100px', height: '100px', marginRight: '10px' }} />
          <div>
            <p>Nome: {contato.first_name} {contato.last_name}</p>
            <p>Email: {contato.email}</p>
            <p>Gênero: {contato.gender}</p>
            <p>Idioma: {contato.language}</p>
            <p>Data de Nascimento: {contato.birthday}</p>
          </div>
          <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
            <button onClick={() => handleEditarClick(contato.id)} style={{ backgroundColor: '#12A146', color: '#FFFFFF', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', marginRight: '5px', marginLeft: '0px', marginTop: '125px', marginBottom: '50px' }}>Editar</button>
            <button onClick={() => handleExcluirClick(contato.id)} style={{ backgroundColor: '#12A146', color: '#FFFFFF', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', marginRight: '5px', marginLeft: '5px', marginTop: '125px', marginBottom: '50px' }}>Excluir</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ListaContatos;
