import React, { useEffect, useState } from 'react';
import AdicionarContato from '../adicionarContato/addContact';
import logo from '../../assets/logo.png';
import addUser from '../../assets/icons/addUser.svg';
import searchUser from '../../assets/icons/search.svg'

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://my.api.mockaroo.com/users.json?key=7ce8e6d0');
        const data = await response.json();
        setUsers(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const showDetails = user => {
    setSelectedUser(user);
  };

  return (
    <main className='container'>
      <div className='listContact'>
        <div className='header'>
          <img className='logo' src={logo}></img>
          <div className='headerFunctions'>
            <img className='addContact' src={addUser}></img>
            <input class="form-control" list="datalistOptions" placeholder="Buscar usuÃ¡rio..."></input>
            <img className='searchUser' src={searchUser}></img>
          </div>

        </div>
        <ul>
          {users.map(user => (
            <li key={user.id}>
              <div className='orgList'>
                <div className='contact'>
                  <img className='imageDetail' src={user.avatar} alt="Avatar" />
                  <p className='nameDetail'>{user.first_name} {user.last_name}</p>
                </div>
                <button type="button" class="btn btn-success btnDetail" onClick={() => showDetails(user)} >Detalhes</button>
              </div>
            </li>
          ))}
        </ul>
        {selectedUser && (
          <div className='showContactDetail'>
            <img className='avatar' src={selectedUser.avatar} alt='Avatar' />
            <p className='name'>{selectedUser.first_name} {selectedUser.last_name}</p>
            <p className='email'>{selectedUser.email}</p>
            <p className='gender'>{selectedUser.gender}</p>
            <p className='id'>{}</p>
          </div>
        )}
      </div>
    </main>
  );
}

export default App;
