import React, { useEffect, useState } from 'react';

const ListaContatos = () => {
  const [contatos, setContatos] = useState([]);

  useEffect(() => {
    const fetchContatos = async () => {
      try {
        const response = await fetch('https://my.api.mockaroo.com/lestetelecom/test.json?key=f55c4060');
        const data = await response.json();
        setContatos(data);
      } catch (error) {
        console.error('Erro ao obter os contatos:', error);
      }
    };

    fetchContatos();
  }, []);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
      {contatos.map(contato => (
        <div key={contato.id} style={{ display: 'flex', border: '1px solid #12A146', padding: '10px' }}>
          <img src={contato.avatar} alt="Avatar" style={{ width: '100px', height: '100px', marginRight: '10px' }} />
          <div>
            <p>Nome: {contato.first_name} {contato.last_name}</p>
            <p>Email: {contato.email}</p>
            <p>GÃªnero: {contato.gender}</p>
            <p>Idioma: {contato.language}</p>
            <p>Data de Nascimento: {contato.birthday}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ListaContatos;
