import React, { useEffect, useState } from 'react';
import AdicionarContato from '../adicionarContato/addContact';
import logo from '../../assets/logo.png';
import addUser from '../../assets/icons/addUser.svg';
import searchUser from '../../assets/icons/search.svg'
import close from '../../assets/icons/close.svg'
import filtro from '../../assets/icons/filtro.svg'

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [filterOptions, setFilterOptions] = useState({
    nome: false,
    birthday: false,
    gender: false,
    idioma: false,
  });
  const [searchText, setSearchText] = useState('');


  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://my.api.mockaroo.com/lestetelecom/test.json?key=f55c4060');
        const data = await response.json();
        setUsers(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const showDetails = user => {
    setSelectedUser(user);
    const element = document.getElementById('showContactDetail');
    if (element) {
      element.style.display = 'flex';
    }
  };

  const closeDetails = () => {
    document.getElementById('showContactDetail').style.display = 'none';
  };

  const searchFilter = () => {
    document.getElementById('filter').style.display = 'flex';
  };

  const closeFilter = () => {
    document.getElementById('filter').style.display = 'none';
  }

  const applyFilter = () => {
    const filteredUsers = users.filter(user => {
      let passFilter = true;

      if (searchText && !user.first_name.toLowerCase().includes(searchText.toLowerCase())) {
        passFilter = false;
      }

      Object.keys(filterOptions).forEach(option => {
        if (selectedUser && filterOptions[option] && user[option] !== selectedUser[option]) {
          passFilter = false;
        }
      });
      return passFilter;
    });
    return filteredUsers;
  };

  return (
    <main className='container'>
      <div className='listContact'>
        <div className='header'>
          <img className='logo' alt='logo' src={logo}></img>
          <div className='headerFunctions'>
            <img className='addContact' alt='Adicionar Usuário' src={addUser}></img>
            <input class="form-control" list="datalistOptions" placeholder="Buscar usuário..." value={searchText} onChange={(e) => setSearchText(e.target.value )}  ></input>
            <img className='searchFilter' alt='Filtro' onClick={searchFilter} src={filtro}></img>
            <img className='searchUser' alt='Buscar Usuário' src={searchUser}></img>
          </div>
          <div className='filter' id='filter'>
            <div class="form-check">
              <input class="form-check-input" type="checkbox" value={filterOptions.nome} id="searchByName" onChange={() => setFilterOptions({ ...filterOptions, nome: !filterOptions.nome })} ></input>
              <label class="form-check-label" for="searchByName">Nome</label>
            </div>
            <div class="form-check">
              <input class="form-check-input" type="checkbox" value={filterOptions.birthday} id="searchByName" onChange={() => setFilterOptions({ ...filterOptions, birthday: !filterOptions.birthday })} ></input>
              <label class="form-check-label" for="searchByName">Nome</label>
            </div>
            <div class="form-check">
              <input class="form-check-input" type="checkbox" value={filterOptions.gender} id="searchByGender" onChange={() => setFilterOptions({ ...filterOptions, gender: !filterOptions.gender })} ></input>
              <label class="form-check-label" for="searchByGender">Gênero</label>
            </div>
            <div class="form-check">
              <input class="form-check-input" type="checkbox" value={filterOptions.idioma} id="searchByIdioma" onChange={() => setFilterOptions({ ...filterOptions, idioma: !filterOptions.idioma })} ></input>
              <label class="form-check-label" for="searchByIdioma">Idioma</label>
            </div>
            <div class="form-check">
              <input class="form-check-input" type="checkbox" value={filterOptions.birthday} id="searchByEmail" onChange={() => setFilterOptions({ ...filterOptions, birthday: !filterOptions.email })} ></input>
              <label class="form-check-label" for="searchByEmail">Email</label>
            </div>
            <div class="form-check">
              <input class="form-check-input" type="checkbox" value={filterOptions.id} id="searchById" onChange={() => setFilterOptions({ ...filterOptions, id: !filterOptions.id })} ></input>
              <label class="form-check-label" for="searchById">ID</label>
            </div>
            <img src={close} className='closeFilter' id='closeFilter' onClick={closeFilter} ></img>
          </div>

        </div>
        <ul>
          {applyFilter().map(user => (
            <li key={user.id}>
              <div className='orgList'>
                <div className='contact'>
                  <img className='imageDetail' src={user.avatar} alt="Avatar" />
                  <p className='nameDetail'>{user.first_name} {user.last_name}</p>
                </div>
                <button type="button" class="btn btn-success btnDetail" onClick={() => showDetails(user)} >Detalhes</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {selectedUser && (
        <div class="alert alert-success alert-dismissible fade show showContactDetail" id='showContactDetail' role="alert">
          <div className='img'>
            <img className='avatar' src={selectedUser.avatar} alt='Avatar' />
          </div>
          <div className='info'>
            <img className='close' alt='close' src={close} onClick={closeDetails} ></img>
            <p className='name'>Nome: {selectedUser.first_name} {selectedUser.last_name}</p>
            <p className='age'>Idade: {selectedUser.birthday}</p>
            <p className='gender'>Gênero: {selectedUser.gender}</p>
            <p className='idioma'>Idioma: {selectedUser.language}</p>
            <p className='email'>Email: {selectedUser.email}</p>
            <p className='id'>ID: {selectedUser.id}</p>
          </div>
        </div>
      )}
    </main>
  );
}

export default App;
