import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../../assets/logo.png';
import ListaContatos from '../ListaContatos';

const HomePage = () => {
  const [showListaContatos, setShowListaContatos] = useState (false);
  const handleButtonClick = () => {
    setShowListaContatos(true);
  }; 
  
  return (
    <div style={{ backgroundColor: '#EBEBEB', minHeight: '100vh', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
      <img src={logo} alt="Logo" style={{ maxWidth: '300px', maxHeight: '200px', marginBottom: '20px' }} />

      <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
      
          <button style={{ backgroundColor: '#12A146', color: '#FFFFFF', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer' }} onClick={handleButtonClick}>Lista de contatos</button>
      
      </div>
    </div>
  );
}

export default HomePage;
