import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const DetalheContato = () => {
  const { id } = useParams();
  const [contato, setContato] = useState(null);

  useEffect(() => {
    const fetchContato = async () => {
      try {
        const response = await fetch(`https://my.api.mockaroo.com/lestetelecom/test/${id}.json?key=f55c4060`);
        const data = await response.json();
        setContato(data);
      } catch (error) {
        console.error('Erro ao obter o contato:', error);
      }
    };

    fetchContato();
  }, [id]);

  if (!contato) {
    return <div>Carregando...</div>;
  }

  return (
    <div>
      <h2>Detalhes do Contato</h2>
      <div>ID: {contato.id}</div>
      <div>Primeiro Nome: {contato.first_name}</div>
      <div>Último Nome: {contato.last_name}</div>
      <div>Email: {contato.email}</div>
      <div>Gênero: {contato.gender}</div>
      <div>Idioma: {contato.language}</div>
      <div>Avatar: {contato.avatar}</div>
    </div>
  );
};

export default DetalheContato;
