import React, { useEffect, useState } from 'react';

const ListaContatos = () => {
    const [contatos, setContatos] = useState([]);
  
    useEffect(() => {
      const fetchContatos = async () => {
        try {
          const response = await fetch('https://my.api.mockaroo.com/lestetelecom/test.json?key=f55c4060');
          const data = await response.json();
          setContatos(data);
        } catch (error) {
          console.error('Erro ao buscar os contatos:', error);
        }
      };
  
      fetchContatos();
    }, []);
  
    const handleEditarClick = (id) => {
      // L贸gica para editar o contato com o ID fornecido
      console.log('Editar contato:', id);
    };
  
    const handleExcluirClick = (id) => {
      // L贸gica para excluir o contato com o ID fornecido
      console.log('Excluir contato:', id);
    };
  
    return (
      <div>
        {contatos.map((contato) => (
          <div key={contato.id} style={{ backgroundColor: '#12A146', color: '#FFFFFF', padding: '10px', marginBottom: '5px' }}>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <img src={contato.avatar} alt={contato.first_name} style={{ width: '50px', height: '50px', borderRadius: '50%', marginRight: '10px' }} />
              <div>
                <p>{contato.first_name} {contato.last_name}</p>
                <p>{contato.email}</p>
                <p>{contato.gender}</p>
              </div>
            </div>
            <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
              <button onClick={() => handleEditarClick(contato.id)} style={{ backgroundColor: '#12A146', color: '#FFFFFF', padding: '5px 10px', border: 'none', borderRadius: '5px', cursor: 'pointer', marginRight: '5px' }}>Editar</button>
              <button onClick={() => handleExcluirClick(contato.id)} style={{ backgroundColor: '#12A146', color: '#FFFFFF', padding: '5px 10px', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Excluir</button>
            </div>
          </div>
        ))}
      </div>
    );
  }
  
  export default ListaContatos;
